<template>
  <div class="container">
    <div class="search">
      <el-input v-model="keyword" size="large" placeholder="输入关键字搜索" />
    </div>
    <div>
      <div v-for="icon in list" :key="icon" class="icon">
        <z-copy :value="icon">
          <component :is="icon" />
          <div class="text">
            {{ icon }}
          </div>
        </z-copy>
      </div>
    </div>
  </div>
</template>

<script>
import Icons from '../src/index.js'

export default {
  components: Icons,
  data() {
    return {
      keyword: null,
      icons: Object.keys(Icons),
    }
  },
  computed: {
    list() {
      if (!this.keyword) {
        return this.icons
      }
      return this.icons.filter(icon => {
        return icon.toLowerCase().indexOf(this.keyword) > -1
      })
    },
  },
}
</script>
