<template>
  <z-copy class="icon" :value="value">
    <component :is="com" size="24" />
  </z-copy>
</template>
<script>
export default {
  props: {
    name: String,
    com: Function,
  },
  computed: {
    value() {
      const comName = this.name.split('-').map(part => {
        // 首字母大写，其余字母保持不变
        return part.charAt(0).toUpperCase() + part.slice(1)
      })
        .join('')
      return `import I${comName} from '@icons/${this.name}'`
    }
  },
}
</script>
<style scoped lang="scss">
.icon {
  display: inline-block;
  border: solid 1px #ebeef5;
  margin: -3px -1px;
  width: 60px;
  height: 60px;
  line-height: 72px;
  text-align: center;
  cursor: pointer;
  overflow: hidden;

  &:hover {
    background: #f1f2f6;
    color: #337ecc;
  }

  .text {
    font-size: 12px;
    color: #868789;
  }
}
</style>