<template>
  <div>
    <el-button-group class="affix">
      <el-button @click="set('colors')">颜色参数</el-button>
      <!-- <el-button @click="set('icons')">内置图标</el-button> -->
      <el-button @click="set('extend')">图标组件</el-button>
    </el-button-group>
    <component :is="components[current]" />
  </div>
</template>
<script>
import { defineComponent } from 'vue';
import colors from './colors.vue';
import extend from './extend.vue';
import icons from './icons.vue';
export default defineComponent({
  data() {
    return { current: 'colors' }
  },
  setup() {
    const components = { colors, extend, icons }
    return { components }
  },
  methods: {
    set(name) {
      this.current = name
    }
  }
})
</script>
