<template>
  <div class="container">
    <el-row>
      <el-col :span="4">
        <el-affix>
          <h3>扩展图标</h3>
          <el-anchor :offset="40">
            <el-anchor-link v-for="category in Categories" :key="category.name" :href="`#${category.name}`">
              {{ category.title }}
            </el-anchor-link>
          </el-anchor>
        </el-affix>
      </el-col>
      <el-col :span="20">
        <div class="search">
          <el-input v-model="keyword" size="large" placeholder="输入关键字搜索" />
        </div>
        <div v-if="keyword">
          <div class="sons">
            <Item v-for="item in list" :key="'-' + item.name" :name="item.name" :com="All[item.name]" />
          </div>
        </div>
        <div v-else>
          <div class="category" v-for="category in Categories" :key="category.name">
            <h4 :id="category.name">{{ category.title }}</h4>
            <div class="sons">
              <Item v-for="name in category.sons" :key="category.name + '-' + name" :name="name" :com="All[name]" />
            </div>
          </div>
        </div>
      </el-col>
    </el-row>
  </div>
</template>

<script>

import { defineComponent } from 'vue'
import All from '../data/all.js'
import Categories from '../data/categories.js'
import AllList from '../data/list.js'
import Item from './item.vue'
export default defineComponent({
  components: { Item },
  data() {
    return {
      keyword: null,
    }
  },
  setup() {
    return { All, Categories }
  },
  computed: {
    list() {
      if (!this.keyword) {
        return []
      }
      return AllList.filter(icon => {
        return icon.name.toLowerCase().indexOf(this.keyword) > -1
      })
    },
  },
})
</script>
<style scoped lang="scss">
h3 {
  margin-left: 10px;
}

.container {
  margin: 10px auto;
}

.search {
  margin-bottom: 14px;
}
</style>
