#!/bin/bash

# 定义源文件目录和目标目录
src_dir="./"
dest_dir="./dist"

# 确保目标目录存在
mkdir -p $dest_dir

# 遍历源文件目录中的所有.js文件
cp -f ./src/* ${dest_dir}/

cp -f ./package-dist.json ${dest_dir}/package.json
cd ${dest_dir}
npm publish
